var Paging = React.createClass({
    render: function () {

        return (
            <div id="paging" className="popUpLayerPaging">
                <span>共<b>1</b>页</span>
                <a id="prePage" onclick="PAGER.gotoPrePage()">上一页</a>
                <a onclick="PAGER.gotoPage(1)">1</a><a id="nextPage" onclick="PAGER.gotoNextPage()">下一页</a>
                <label>至</label>
                <input type="text" value="" className="inputW20H16" id="inputPageNo" name="inputPageNo"/>
                <label>页</label>
                <a href="javascript:void(0);" className="fontC36cS12 jump" title=""
                   onclick="PAGER.gotoPage(inputPageNo.value)">跳转</a>
            </div>
        );
    }
});

var DataLi = React.createClass({
    render: function () {
        var STAFF_CODE = this.props.myData.STAFF_CODE;
        return (<li>{STAFF_CODE}</li>);
    }
});

var DataList = React.createClass({
    render: function () {
        var rows = [];
        this.props.dataList.forEach(function (data) {
            rows.push(<DataLi myData={data} key={data.STAFF_CODE}/>);
        });
        return (<ul id="Pui">
            {rows}
        </ul>);
    }
});

var MyPager = React.createClass({
    render: function () {
        return (<div className="my-pager">
            <div className="staffCon">
                <DataList dataList={this.props.dataList}/>
            </div>
            <Paging dataList={this.props.dataList} />
        </div>);
    }
});

ReactDOM.render(
    <MyPager dataList={data.staffList}/>
    , document.getElementById("content"));
